const { ethers } = require('ethers');

const provider = new ethers.JsonRpcProvider(process.env.BASE_RPC || 'https://mainnet.base.org');
const registryAddress = '0x3081aE79B403587959748591bBe1a2c12AeF5167';
const myAddress = '0x49FE2f235675c5a62096A658447B2E8c20B50B89';

