  'function agents(address) view returns (string repoUrl, address wallet, string name, uint256 registeredAt, uint256 lastSeen)',
  'function count() view returns (uint256)'
];

const contract = new ethers.Contract(registryAddress, abi, provider);

async function check() {
  const count = await contract.count();
  console.log('Total agents:', count.toString());

  const agent = await contract.agents(myAddress);
  console.log('My registration:');
  console.log('  repoUrl:', agent.repoUrl);
  console.log('  name:', agent.name);
  console.log('  registeredAt:', agent.registeredAt.toString());
  console.log('  lastSeen:', agent.lastSeen.toString());
}

check().catch(console.error);
